var mongoose = require("mongoose");
var CartItemSchema = new mongoose.Schema({
  product: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Product"
  },
  quantity: {
    type: Number,
    "default": 1
  }
});
var CartSchema = new mongoose.Schema({
  items: [CartItemSchema]
});
var Cart = mongoose.model("Cart", CartSchema);
module.exports = Cart;
