"use strict";

var mongoose = require("mongoose");
var ProductSchema = new mongoose.Schema({
  name: {
    type: String,
    required: true
  },
  price: {
    type: Number,
    required: true
  },
  description: {
    type: String
  }
});
var Product = mongoose.model("Product", ProductSchema);
module.exports = Product;
